{
  path: 'portfolio',
  template: 'src/containers/Portfolio',
  getData: async () => ({
    portfolio,
  }),
},

// A route with data and dynamically generated child routes
{
  path: 'blog',
  template: 'src/containers/Blog',
  getData: async () => ({
    posts,
  }),
  children: posts.map(post => ({
    path: `post/${post.slug}`,
    template: 'src/containers/BlogPost',
    getData: async () => ({
      post,
    }),
  })),
},

// A 404 component
{
  path: '404',
